package basic.program;

public class Divisible7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=17;
		if(a%7==0)
		{
			System.out.println("**Number is divisible by 7**");
		}
		else
		{
			System.out.println("**Not divisible by 7**");
		}
	}

}
