package basic.program;

public class Pattern15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=1;i<=4;i++)
		{
			for(int j=1;j<=8;j=j*2)
			{
				if(j%2==0)
				{
					System.out.print("#");
				}
			}
			System.out.println();

	}

}
}